describe('/GET rooms',() =>{
    it('GET all the rooms',(done) =>{
        chai.request(app)
            .get('api/v1/rooms')
            .end((err,res) =>{
                if (err) console.log(err);
                res.should.have.status(200);
                res.body.should.be.a('array');
                done();
            })
    })
})

