import mongoose from "mongoose";
import moment from "moment";

const { Schema } = mongoose;

// Create Room's Schema
const RoomSchema = new Schema({
  roomName: {
    type: String,
    required: true,
  },
  maxPerson: {
    type: Number,
    min: 1,
    max: 8,
  },
  roomFeatures: [String],
  bedOptions: [String],
  descriptions: String,
  roomImage: {
    type:String,
    default:null
  },
  isReserved: {
    type: Boolean,
